<script src="http://163pinglun.com/wp-content/themes/163pinglun/wow.js"></script>
<script>
$(function(){
	new WOW().init();
	$('div.slide').each(function(i){
		var width = $(this).data("width");
		var height = $(this).data("height");
		$(this).slidesjs({
			width: width,
			height: height,
			pagination: false
		});

		var api = $(this).data('plugin_slidesjs');
		$(this).on('click', 'div.slidesjs-control', api.next.bind(api));
	});
});
</script>
